/**
 * Image Blur Placeholders
 * Base64-encoded blurred versions of images for better perceived performance
 *
 * These can be generated using:
 * 1. Online: https://www.nuget.org/packages/BlurHash.Core/
 * 2. CLI: plaiceholder library
 * 3. Squoosh: https://squoosh.app/
 */

export const imageBlurs = {
  // Hero Images
  heroMain: "data:image/jpeg;base64,",

  // Product Images
  stackParking: "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChwGA60e6kgAAAABJRU5ErkJggg==",
  puzzleParking: "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChwGA60e6kgAAAABJRU5ErkJggg==",
  rotaryParking: "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChwGA60e6kgAAAABJRU5ErkJggg==",

  // Background Images
  servicesBackground: "data:image/jpeg;base64,",
  clientsBackground: "data:image/jpeg;base64,",
};

/**
 * Get blur data for an image
 * @param imageKey - Key from imageBlurs object
 * @returns Base64 blur data or empty string
 */
export function getBlurDataUrl(imageKey: keyof typeof imageBlurs): string {
  return imageBlurs[imageKey] || "";
}

/**
 * Note: To generate proper blur data for your images:
 *
 * Option 1: Use Plaiceholder (recommended)
 * npm install plaiceholder
 *
 * import { getPlaiceholder } from 'plaiceholder';
 *
 * const getBlur = async (src: string) => {
 *   const { base64, img } = await getPlaiceholder(src);
 *   return base64;
 * };
 *
 * Option 2: Use Squoosh CLI
 * npm install @squoosh/cli -g
 * squoosh-cli --resize '{mode: "cover", size: {width: 100, height: 100}}' image.jpg
 *
 * Option 3: Use online tool
 * https://www.nuget.org/packages/BlurHash.Core/
 */
